import React from 'react';
import { Box, Button, Typography } from '@mui/material';
import Slider from 'react-slick'; // Importa el componente Slider

const WeatherPage = () => {
  const handleGenerateReport = () => {
    // Lógica para generar el reporte de clima
    console.log('Report generated');
  };

  // Configuración del carrusel
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true, // Habilita el autoplay
    autoplaySpeed: 3000, // Duración entre cambios de imagen (en milisegundos)
  };

  // Lista de imágenes para el carrusel
  const images = [
    'url_de_la_imagen_1.jpg',
    'url_de_la_imagen_2.jpg',
    'url_de_la_imagen_3.jpg',
    'url_de_la_imagen_4.jpg',
  ];

  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        height: '100vh',
        textAlign: 'center',
        p: 3
      }}
    >
      <Typography variant="h4" gutterBottom>
        Weather Control
      </Typography>
      
      {/* Carrusel de imágenes */}
      <Slider {...settings} style={{ width: '80%', marginBottom: '20px' }}>
        {images.map((image, index) => (
          <div key={index}>
            <img src={image} alt={`Weather Image ${index + 1}`} style={{ width: '100%', height: 'auto' }} />
          </div>
        ))}
      </Slider>
      
      <Typography variant="body1" gutterBottom>
        Click the button below to generate the weather report.
      </Typography>
      <Button
        variant="contained"
        color="primary"
        onClick={handleGenerateReport}
        sx={{ mt: 3 }}
      >
        Generate Report
      </Button>
    </Box>
  );
};

export default WeatherPage;
