import React from 'react';
import { Box, Button, Typography } from '@mui/material';
import { images } from "../../assets"; // Importa las imágenes
import Carousel from 'react-slick'; // Asegúrate de tener instalada esta dependencia

const WeatherPage = () => {
  const handleGenerateReport = () => {
    // Lógica para generar el reporte de clima
    console.log('Report generated');
  };

  const carouselSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true, // Habilitar autoplay
    autoplaySpeed: 2000, // Velocidad de autoplay
  };

  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        height: '100vh',
        textAlign: 'center',
        p: 3
      }}
    >
      <Typography variant="h4" gutterBottom>
        Weather Control
      </Typography>
      <Typography variant="body1" gutterBottom>
        Click the button below to generate the weather report.
      </Typography>

      {/* Carrusel de imágenes */}
      <Carousel {...carouselSettings}>
        <div>
          <img src={images.weatherImages.image1} alt="" style={{ width: '100%', height: 'auto' }} />
        </div>
        <div>
          <img src={images.weatherImages.image2} alt="" style={{ width: '100%', height: 'auto' }} />
        </div>
        <div>
          <img src={images.weatherImages.image3} alt="" style={{ width: '100%', height: 'auto' }} />
        </div>
        <div>
          <img src={images.weatherImages.image4} alt="" style={{ width: '100%', height: 'auto' }} />
        </div>
      </Carousel>

      <Button
        variant="contained"
        color="primary"
        onClick={handleGenerateReport}
        sx={{ mt: 3 }}
      >
        Generate Report
      </Button>
    </Box>
  );
};

export default WeatherPage;
