import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import Slider from "react-slick"; // Importa el carrusel
import ChatBubbleOutlineOutlinedIcon from '@mui/icons-material/ChatBubbleOutlineOutlined';
import DashboardCustomizeOutlinedIcon from '@mui/icons-material/DashboardCustomizeOutlined';
import DirectionsCarFilledOutlinedIcon from '@mui/icons-material/DirectionsCarFilledOutlined';
import MailOutlinedIcon from '@mui/icons-material/MailOutlined';
import NotificationsOutlinedIcon from '@mui/icons-material/NotificationsOutlined';
import OtherHousesOutlinedIcon from '@mui/icons-material/OtherHousesOutlined';
import SavingsOutlinedIcon from '@mui/icons-material/SavingsOutlined';
import SportsMotorsportsOutlinedIcon from '@mui/icons-material/SportsMotorsportsOutlined';
import SwapHorizOutlinedIcon from '@mui/icons-material/SwapHorizOutlined';
import CloudOutlinedIcon from '@mui/icons-material/CloudOutlined';
import InfoOutlinedIcon from '@mui/icons-material/InfoOutlined';
import ExitToAppOutlinedIcon from '@mui/icons-material/ExitToAppOutlined';
import { Box, Drawer, List, ListItem, ListItemButton, ListItemIcon, ListItemText, Paper, Typography, colors } from '@mui/material';
import { images } from "../../assets"; // Asegúrate de tener las imágenes aquí
import Animate from "./Animate";

const menus = [
  {
    title: "Dashboard",
    icon: <DashboardCustomizeOutlinedIcon />,
    state: "overview",
    path: "/dashboard"
  }
];

const serviceMenus = [
  {
    title: "Info",
    icon: <InfoOutlinedIcon />,
    state: "info",
    path: "/dashboard/info" // Cambiado para incluir '/dashboard/'
  },
  {
    title: "Weather Control",
    icon: <CloudOutlinedIcon />,
    state: "weather",
    path: "/dashboard/weather" // Cambiado para incluir '/dashboard/'
  }
];

const Sidebar = ({ sidebarWidth }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const activeState = location.pathname;

  const handleLogout = () => {
    console.log("User logged out");
    navigate("/");
  };

  const MenuItem = ({ item, isActive }) => {
    const handleClick = () => {
      if (item.path) {
        navigate(item.path);
      }
    };

    return (
      <ListItem disableGutters disablePadding sx={{ py: 0.5 }}>
        <ListItemButton
          onClick={handleClick}
          sx={{
            borderRadius: "10px",
            bgcolor: isActive ? colors.green[600] : "",
            color: isActive ? colors.common.white : "",
            "&:hover": {
              bgcolor: isActive ? colors.green[600] : "",
              color: isActive ? colors.common.white : "",
            }
          }}>
          <ListItemIcon sx={{
            minWidth: "40px",
            color: isActive ? colors.common.white : ""
          }}>
            {item.icon}
          </ListItemIcon>
          <ListItemText primary={<Typography fontWeight={600}>{item.title}</Typography>} />
        </ListItemButton>
      </ListItem>
    );
  };

  const drawer = (
    <Box
      padding={3}
      paddingBottom={0}
      display="flex"
      flexDirection="column"
      height="100vh"
      sx={{
        "::-webkit-scrollbar": {
          display: "none"
        }
      }}
    >
      {/* logo */}
      <Box sx={{ textAlign: "center", mb: 2 }}>
        <Animate type="fade" delay={1}>
          <img src={images.logo} alt="logo" height={60} />
        </Animate>
      </Box>
      {/* Carrusel de imágenes */}
      <Box sx={{ mb: 2 }}>
        <Slider dots={true} infinite={true} speed={500} slidesToShow={1} slidesToScroll={1}>
          {/* Aquí debes agregar las imágenes del carrusel */}
          <div><img src={images.image1} alt="imagen1" style={{ width: '100%', borderRadius: '10px' }} /></div>
          <div><img src={images.image2} alt="imagen2" style={{ width: '100%', borderRadius: '10px' }} /></div>
          <div><img src={images.image3} alt="imagen3" style={{ width: '100%', borderRadius: '10px' }} /></div>
          <div><img src={images.image4} alt="imagen4" style={{ width: '100%', borderRadius: '10px' }} /></div>
        </Slider>
      </Box>
      {/* Menú */}
      <Animate sx={{ flexGrow: 1 }}>
        <Paper
          elevation={0}
          square
          sx={{
            borderTopRightRadius: "10px",
            borderTopLeftRadius: "10px",
            p: 2,
            height: "100%",
            boxShadow: "rgba(145, 158, 171, 0.2) 0px 0px 2px 0px, rgba(145, 158, 171, 0.12) 0px 12px 24px -4px"
          }}
        >
          <List>
            {menus.map((item, index) => (
              <MenuItem
                key={index}
                item={item}
                isActive={activeState.includes(item.path)}
              />
            ))}
          </List>
          <List>
            <ListItem>
              <Typography fontWeight={600} mt={1} color={colors.grey[600]}>
                Services
              </Typography>
            </ListItem>
            {serviceMenus.map((item, index) => (
              <MenuItem
                key={index}
                item={item}
                isActive={activeState.includes(item.path)}
              />
            ))}
          </List>
          <List>
            <ListItem disableGutters disablePadding sx={{ py: 0.5 }}>
              <ListItemButton
                onClick={handleLogout}
                sx={{
                  borderRadius: "10px",
                  bgcolor: colors.red[600],
                  color: colors.common.white,
                  "&:hover": {
                    bgcolor: colors.red[800],
                    color: colors.common.white,
                  }
                }}
              >
                <ListItemIcon sx={{ minWidth: "40px", color: colors.common.white }}>
                  <ExitToAppOutlinedIcon />
                </ListItemIcon>
                <ListItemText primary={
                  <Typography fontWeight={600}>
                    Logout
                  </Typography>
                } />
              </ListItemButton>
            </ListItem>
          </List>
        </Paper>
      </Animate>
    </Box>
  );

  return (
    <Box
      component="nav"
      sx={{
        width: { md: sidebarWidth },
        flexShrink: { md: 0 }
      }}
    >
      <Drawer
        variant="permanent"
        sx={{
          display: { xs: "none", sm: "none", md: "block" },
          "& .MuiDrawer-paper": {
            boxSizing: "border-box",
            width: sidebarWidth,
            borderWidth: 0,
            bgcolor: "transparent",
            "::-webkit-scrollbar": {
              display: "none"
            }
          }
        }}
        open
      >
        {drawer}
      </Drawer>
    </Box>
  );
};

export default Sidebar;
