import React from 'react';
import { Box, Grid, Stack, Typography, colors } from '@mui/material';
import Animate from "../components/common/Animate";
import MPaper from '../components/common/MPaper';
import imagen1 from '../assets/images/imagen1.jpg';
import imagen2 from '../assets/images/imagen2.jpg';
import imagen3 from '../assets/images/imagen3.jpg';
import imagen4 from '../assets/images/imagen4.jpg';

// Datos del resumen
const summaryData = [
  {

    image: imagen1 // Usando una de las imágenes
  },
  {

    image: imagen2 // Usando una de las imágenes
  },
  {

    image: imagen3 // Usando una de las imágenes
  }
];

const WeatherPage = () => {
  return (
    <Grid container spacing={3}>
      {summaryData.map((summary, index) => (
        <Grid key={index} item xs={12} lg={4}>
          <Animate type="fade" delay={(index + 1) / 3}>
            <MPaper>
              <Stack direction="row" alignItems="center" justifyContent="space-between">
                <Stack spacing={1}>
                  <Typography variant="h4" fontWeight="bold">
                    {summary.value}
                  </Typography>
                  <Typography variant="body2" fontWeight="bold" color={colors.grey[600]}>
                    {summary.title}
                  </Typography>
                </Stack>
                <Box sx={{ width: '150px', height: '150px', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                  {/* Imagen centrada dentro de Box */}
                  <img src={summary.image} alt={summary.title} style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
                </Box>
              </Stack>
            </MPaper>
          </Animate>
        </Grid>
      ))}
    </Grid>
  );
};

export default WeatherPage;
