import React from 'react';
import { createBrowserRouter, RouterProvider } from 'react-router-dom';
import AppLayout from './components/layout/AppLayout';  // Ruta del AppLayout
import MainLayout from './components/layout/MainLayout'; // Ruta del MainLayout
import DashboardPage from './pages/DashboardPage'; // Ruta del DashboardPage
import Weather from './pages/Weather'; // Ruta del Weather
import Info from './pages/Info'; // Ruta del Info

const router = createBrowserRouter([
  {
    path: "/",
    element: <AppLayout />, // El layout base de la aplicaci칩n
    children: [
      {
        path: "dashboard",
        element: <MainLayout />, // El layout principal con el Sidebar
        children: [
          { path: "", element: <DashboardPage /> }, // P치gina por defecto del dashboard
          { path: "weather", element: <Weather /> }, // Ruta del componente Weather
          { path: "info", element: <Info /> } // Ruta del componente Info
        ],
      },
    ],
  },
]);

const App = () => {
  return <RouterProvider router={router} />;
};

export default App;
