import React from 'react';
import { Box, Typography } from '@mui/material';
import Carousel from 'react-slick'; // Asegúrate de tener instalada esta dependencia

import imagen1 from '../assets/images/imagen1.jpg';
import imagen2 from '../assets/images/imagen2.jpg';
import imagen3 from '../assets/images/imagen3.jpg';
import imagen4 from '../assets/images/imagen4.jpg';

const WeatherPage = () => {
  const carouselSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true, // Habilitar autoplay
    autoplaySpeed: 4000, // Velocidad de autoplay (4 segundos)
    arrows: false, // Desactivar botones de navegación
  };

  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        height: '100vh',
        textAlign: 'center',
        p: 3
      }}
    >
      <Typography variant="h4" gutterBottom>
        Weather Control
      </Typography>
      <Typography variant="body1" gutterBottom>
        Click the button below to generate the weather report.
      </Typography>

      {/* Carrusel de imágenes */}
      <Carousel {...carouselSettings}>
        <div>
          <img src={imagen1} alt="Imagen 1" style={{ width: '100%', height: 'auto' }} />
        </div>
        <div>
          <img src={imagen2} alt="Imagen 2" style={{ width: '100%', height: 'auto' }} />
        </div>
        <div>
          <img src={imagen3} alt="Imagen 3" style={{ width: '100%', height: 'auto' }} />
        </div>
        <div>
          <img src={imagen4} alt="Imagen 4" style={{ width: '100%', height: 'auto' }} />
        </div>
      </Carousel>
    </Box>
  );
};

export default WeatherPage;
