import React from 'react';
import { Box, Grid, Stack, Typography, colors } from '@mui/material';
import Animate from "../components/common/Animate";
import MPaper from '../components/common/MPaper';
import Carousel from 'react-slick'; // Asegúrate de tener instalada esta dependencia
import imagen1 from '../assets/images/imagen1.jpg';
import imagen2 from '../assets/images/imagen2.jpg';
import imagen3 from '../assets/images/imagen3.jpg';
import imagen4 from '../assets/images/imagen4.jpg';

// Datos del resumen
const summaryData = [
  {
    title: "Daily income ",
    value: "Q.360.00",
    images: [imagen1, imagen2] // Cambiado a un array para el carrusel
  },
  {
    title: "Parked Today",
    value: "120",
    images: [imagen3, imagen4] // Cambiado a un array para el carrusel
  },
  {
    title: "Parked Outside",
    value: "235",
    images: [imagen1, imagen3] // Cambiado a un array para el carrusel
  }
];

const WeatherPage = () => {
  // Configuración del carrusel
  const carouselSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 4000,
    arrows: false, // Quitar botones de navegación
  };

  return (
    <Grid container spacing={3}>
      {summaryData.map((summary, index) => (
        <Grid key={index} item xs={12} lg={4}>
          <Animate type="fade" delay={(index + 1) / 3}>
            <MPaper>
              <Stack direction="row" alignItems="center" justifyContent="space-between">
                <Stack spacing={1}>
                  <Typography variant="h4" fontWeight="bold">
                    {summary.value}
                  </Typography>
                  <Typography variant="body2" fontWeight="bold" color={colors.grey[600]}>
                    {summary.title}
                  </Typography>
                </Stack>
                <Box sx={{ width: '100px', height: '100px' }}>
                  {/* Carrusel de imágenes dentro de Box */}
                  <Carousel {...carouselSettings}>
                    {summary.images.map((image, imgIndex) => (
                      <div key={imgIndex}>
                        <img src={image} alt={`summary-${imgIndex}`} style={{ width: '100%', height: 'auto' }} />
                      </div>
                    ))}
                  </Carousel>
                </Box>
              </Stack>
            </MPaper>
          </Animate>
        </Grid>
      ))}
    </Grid>
  );
};

export default WeatherPage;
